mapper.update:
UPDATE transaction
PAYMENT_OPTION = #{paymentOption}, RECEIVER_NAME = #{receiverName}, RECEIVER_PHONE = #{receiverPhone}, DEMAILADDR = #{divyAddr}, DLVY_REQUEST = #{divyRequest}, DLVY_DATE = #{divyDate}, item = #{item}
WHERE TRAN_NO = #{tranNo}

UPDATE transaction
SET TRAN_STATUS_CODE = #{tranCode:VARCHAR} 
WHERE TRAN_NO = #{tranNo:NUMERIC}

mapper.select:
  	SELECT COUNT(*)
  	FROM(	SELECT *
					FROM transaction 
					WHERE buyer_id = #{buyerId}
		 ) countTable

  	SELECT COUNT(*)
  	FROM(	SELECT *
					FROM cart 
					WHERE user_id = #{userId}
		) countTable

  	SELECT COUNT(*)
  	FROM(	select * from TRANSACTION where TRAN_STATUS_CODE = 6
		) countTable
